import React, { Component } from 'react'
import { Grommet } from 'grommet';
import Terminal from 'react-console-emulator'

const commands = {
  echo: {
    description: 'Echo a passed string.',
    usage: 'echo <string>',
    fn: function ()
    {
      return `${Array.from(arguments).join(' ')}`
    }
  }
}

export default class App extends Component
{
  render ()
  {
    return (
      <Grommet themeMode="dark" >
        <Terminal
          // styleEchoBack={true}
          contentStyle={{ 'overflow-x': 'hidden', 'word-wrap': 'break-word' }}
          style={{ borderRadius: '0' }}
          inputAreaStyle={{
            outline: 'yes',
            width: '50%', height: '50%',
            'word-wrap': 'break-word'
          }}
          // inputStyle={{ flex: 0, 'word-wrap': 'break-word' }}
          // inputAreaStyle={{ flexGrow: '0', flex: 0, 'word-wrap': 'break-word' }}
          // messageStyle={{ flex: 0, 'word-wrap': 'break-word' }}
          commands={commands}
          promptLabel={'> '}
          noAutoScroll={true}
        />
      </Grommet>
    )
  }
}
