import React, { Component } from 'react'
import { Grommet } from 'grommet';
import Terminal from 'react-console-emulator'

const commands = {
  echo: {
    description: 'Echo a passed string.',
    usage: 'echo <string>',
    fn: function ()
    {
      return `${Array.from(arguments).join(' ')}`
    }
  }
}

export default class App extends Component
{
  render ()
  {
    return (
      <Grommet themeMode="dark" >
        <Terminal
          // styleEchoBack={true}
          contentStyle={{ padding: '0', border: '0', 'overflow-x': 'hidden' }}
          style={{ height: '100%', width: '100%', maxHeight: '100%', maxWidth: '100%', borderRadius: '0' }}
          inputAreaStyle={{ flex: 0, 'word-wrap': 'break-word' }}
          inputStyle={{ flexGrow: '0', flex: 0, 'word-wrap': 'break-word' }}
          messageStyle={{ flex: 0, 'word-wrap': 'break-word' }}
          commands={commands}
          promptLabel={'> '}
          noAutoScroll={true}
        />
      </Grommet>
    )
  }
}
