import React, { Component } from 'react'
import { Grommet } from 'grommet';
import Terminal from 'react-console-emulator'

const commands = {
  echo: {
    description: 'Echo a passed string.',
    usage: 'echo <string>',
    fn: function ()
    {
      return `${Array.from(arguments).join(' ')}`
    }
  }
}

export default class App extends Component
{
  render ()
  {
    return (
      <Grommet themeMode="dark" >
        <Terminal
          // styleEchoBack={true}
          contentStyle={{ padding: '0', border: '0', 'overflow-x': 'hidden' }}
          style={{ height: '100%', width: '100%', maxHeight: '100%', maxWidth: '100%', borderRadius: '0' }}
          // contentStyle={{ height: '100%', width: 'flex' }}
          // inputAreaStyle={{ height: '100%', width: 'flex' }}
          // promptLabelStyle={{ height: '100%', width: 'flex' }}
          inputStyle={{ flexGrow: 1, flex: 1, flexWrap: 'wrap', height: 'auto' }}
          messageStyle={{ flex: 1, 'word-wrap': 'break-word' }}
          commands={commands}
          promptLabel={'> '}
          noAutoScroll={true}
        />
      </Grommet>
    )
  }
}
