import React, { Component } from 'react'
import Terminal from 'react-console-emulator'

const commands = {
  echo: {
    description: 'Echo a passed string.',
    usage: 'echo <string>',
    fn: function ()
    {
      return `${Array.from(arguments).join(' ')}`
    }
  }
}

export default class MyTerminal extends Component
{
  render ()
  {
    return (
      // <div style={{ height: '100vh', width: '100vw' }}>
      <Terminal
        // contentStyle={{ height: '100%', width: '100%' }}
        style={{
          height: '100%', width: '100%', maxHeight: '100%', maxWidth: '100%'
        }}
        // contentStyle={{ height: '100%', width: 'flex' }}
        // inputAreaStyle={{ height: '100%', width: 'flex' }}
        // promptLabelStyle={{ height: '100%', width: 'flex' }}
        // inputStyle={{ height: '100%', width: 'flex' }}
        // messageStyle={{ height: '100%', width: 'flex' }}
        commands={commands}
        promptLabel={'> '}
        noAutoScroll={true}
      />
      // </div >
    )
  }
}
